            Assert.IsNotNull(conjugationTable.PreteriteTu); Assert.AreEqual("echaste", conjugationTable.PreteriteTu.ToString());
            Assert.IsNotNull(conjugationTable.ImperfectTu); Assert.AreEqual("echabas", conjugationTable.ImperfectTu.ToString());
            Assert.IsNotNull(conjugationTable.ConditionalTu); Assert.AreEqual("echarías", conjugationTable.ConditionalTu.ToString());
